<?php $sname = isset($app['GET']['sname']) ? $app['GET']['sname'] : 'general';$sname1 = isset($app['POST']['sname1']) ? $app['POST']['sname1'] : 'general';$action = isset($app['GET']['action']) ? $app['GET']['action'] : 'list';$section = isset($app['GET']['section']) ? $app['GET']['section'] : 'list';$setting_type = isset($app['REQUEST']['setting_type']) ? $app['REQUEST']['setting_type'] : 'general';$id = isset($app['GET']['id']) ? $app['GET']['id'] : '0';
#handle actions here.
switch ($action):   
 case'list':        $ob = getSettingsByName($sname);        if ($sname == 'robots'):            if (file_exists(DIR_FS_SITE . 'robots.txt')):                $robots_value = trim(file_get_contents(DIR_FS_SITE . 'robots.txt'));            else:                $robots_value = '';            endif;        endif;        break;    case'update':        if (isset($app['POST']['Submit'])):            foreach ($app['POST']['key'] as $k => $v):                $QueryObj = new query('setting');                $QueryObj->Data['id'] = $k;                $QueryObj->Data['value'] = $v;                $QueryObj->Update();            endforeach;            set_alert('success', "Settings have been updated");            Redirect(app_url('setting', '', '', array('sname'=>$sname1),true));        endif;        break;    default:break;endswitch;?>